
public class program5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		{
			int a=Integer.parseInt(args[0]);
			
			if(a%2==0)
			{
				System.out.println("Given number is Even");
			}
			else
			{
				System.out.println("Given number is Odd");
			}	
		}
	}

}
